import React, { useState, useEffect } from 'react';
import { PlusCircle } from 'lucide-react';
import { getTrades } from '../services/api';
import { Button } from '../components/ui/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/Card';
import CumulativeProfitChart from '../components/CumulativeProfitChart';
import TradeCard from '../components/TradeCard';

const DashboardPage = () => {
    const [trades, setTrades] = useState([]);

    useEffect(() => {
        loadTrades();
    }, []);

    const loadTrades = async () => {
        try {
            const response = await getTrades();
            setTrades(response.data);
        } catch (error) {
            console.error("Failed to load trades:", error);
        }
    };

    const totalProfit = trades.reduce((acc, trade) => acc + (parseFloat(trade.resultado) || 0), 0);
    const totalTrades = trades.length;
    const winRate = totalTrades > 0 ? (trades.filter(t => t.resultado > 0).length / totalTrades) * 100 : 0;

    return (
        <div className="flex flex-col h-screen">
            <header className="sticky top-0 z-10 flex items-center justify-between h-16 px-4 border-b shrink-0 bg-background md:px-6">
                <h1 className="text-lg font-semibold">Trade Journal</h1>
                <Button>
                    <PlusCircle className="w-4 h-4 mr-2" />
                    Novo Trade
                </Button>
            </header>

            <main className="flex-1 overflow-auto p-4 md:p-8">
                <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4 mb-8">
                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between pb-2 space-y-0">
                            <CardTitle className="text-sm font-medium">Lucro Total</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className={`text-2xl font-bold ${totalProfit >= 0 ? 'text-green-500' : 'text-red-500'}`}>
                                R$ {totalProfit.toFixed(2)}
                            </div>
                        </CardContent>
                    </Card>
                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between pb-2 space-y-0">
                            <CardTitle className="text-sm font-medium">Taxa de Acerto</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{winRate.toFixed(1)}%</div>
                        </CardContent>
                    </Card>
                    <Card>
                        <CardHeader className="flex flex-row items-center justify-between pb-2 space-y-0">
                            <CardTitle className="text-sm font-medium">Total de Trades</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{totalTrades}</div>
                        </CardContent>
                    </Card>
                </div>

                <div className="grid gap-8 lg:grid-cols-2">
                    <div className="lg:col-span-2">
                        <CumulativeProfitChart trades={trades} />
                    </div>

                    <div className="lg:col-span-2">
                        <h2 className="mb-4 text-2xl font-bold">Hist√≥rico de Trades</h2>
                        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                            {trades.map(trade => (
                                <TradeCard key={trade.id} trade={trade} />
                            ))}
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default DashboardPage;
